import { MapContainer, TileLayer, Marker, Circle, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { locationPins } from "../data/locationPins";
import L from "leaflet";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: markerIcon,
  shadowUrl: markerShadow
});

export default function MapView() {
  const navigate = useNavigate();
  const data = JSON.parse(localStorage.getItem("planTravelData"));

  const [center, setCenter] = useState([20, 78]);
  const [radiusMeters, setRadiusMeters] = useState(0);
  const [pins, setPins] = useState([]);

  useEffect(() => {
    if (!data?.location || !data?.radius) return;

    const city = data.location;
    const radius = Number(data.radius) * 1000;

    // Simple center mapping
    const cityCenters = {
      Goa: [15.49, 73.82],
      Mumbai: [18.96, 72.82],
      Delhi: [28.61, 77.23]
    };

    setCenter(cityCenters[city] || [20, 78]);
    setRadiusMeters(radius);
    setPins(locationPins[city] || []);
  }, [data]);

  return (
    <div style={{ height: "90vh" }}>
      <MapContainer center={center} zoom={6} style={{ height: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* ‚úÖ RADIUS CIRCLE */}
        {radiusMeters > 0 && (
          <Circle
            center={center}
            radius={radiusMeters}
            pathOptions={{ color: "#00eaff" }}
          />
        )}

        {/* ‚úÖ STATIC TOURIST PINS */}
        {pins.map((p, i) => (
          <Marker key={i} position={[p.lat, p.lon]}>
            <Popup>
              <strong>{p.name}</strong><br /><br />
              <button
                onClick={() => {
                  localStorage.setItem(
                    "selectedPlace",
                    JSON.stringify({
                      location: p.name,
                      days: data.days
                    })
                  );
                  navigate("/itinerary");
                }}
              >
                üìç Show Itinerary
              </button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
