import { useEffect, useState } from "react";
import { getCityCoords, getPlaces } from "../services/openTripMap";

export default function Itinerary() {
  const data =
    JSON.parse(localStorage.getItem("fixedData")) ||
    JSON.parse(localStorage.getItem("planTravelData"));

  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadItinerary() {
      const coords = await getCityCoords(data.location);
      if (!coords) return;

      const result = await getPlaces(coords.lat, coords.lon);
      setPlaces(result.slice(0, data.days));
      setLoading(false);
    }

    loadItinerary();
  }, [data.location, data.days]);

  if (loading) {
    return <h2 style={{ textAlign: "center" }}>Generating itinerary‚Ä¶</h2>;
  }

  return (
    <div className="itinerary-container">
      <h2 className="itinerary-title">
        Your {data.location} Itinerary
      </h2>

      {places.map((p, i) => (
        <div key={i} className="day-card">
          <h3>Day {i + 1}</h3>
          <p>üìç {p.name}</p>
          <p>‚≠ê Rating: {p.rate || "N/A"}</p>
        </div>
      ))}

      <div className="itinerary-actions">
        <button className="primary-btn">üíæ Save</button>
        <button className="primary-btn">üîÑ Regenerate</button>
        <button className="primary-btn">‚ùå Discard</button>
      </div>
    </div>
  );
}
