import { generateItinerary } from "../utils/itineraryGenerator";

export default function Itinerary() {
  const data =
    JSON.parse(localStorage.getItem("planTravelData")) ||
    JSON.parse(localStorage.getItem("fixedData")) ||
    { location: "Mysuru", days: 2 };

  const plan = generateItinerary(data.location || "Mysuru", data.days || 2);

  return (
    <div className="itinerary-container">
      <h2 className="itinerary-title">Your Itinerary</h2>

      {plan.map((p, i) => (
        <div key={i} className="day-card">
          <h3>Day {p.day}</h3>
          <p>üìç Visit: {p.place}</p>
          <p>üí∞ Estimated Cost: ‚Çπ2,500</p>
        </div>
      ))}

      <div className="itinerary-actions">
        <button>üíæ Save</button>
        <button>üîÑ Regenerate</button>
        <button>‚ùå Discard</button>
      </div>
    </div>
  );
}
