import { MapContainer, TileLayer, Marker, Popup, Circle } from "react-leaflet";
import { useEffect, useState } from "react";
import { geocodeLocation } from "../services/geocode";
import "leaflet/dist/leaflet.css";

export default function MapView() {
  const [center, setCenter] = useState(null);
  const [radius, setRadius] = useState(0);

  const data = JSON.parse(localStorage.getItem("planTravelData"));

  // Sample destinations (static dataset for PBL)
  const destinations = [
    { name: "Mysuru", lat: 12.2958, lon: 76.6394 },
    { name: "Chennai", lat: 13.0827, lon: 80.2707 },
    { name: "Coimbatore", lat: 11.0168, lon: 76.9558 }
  ];

  useEffect(() => {
    async function load() {
      const geo = await geocodeLocation(data.location);
      if (geo) {
        setCenter([geo.lat, geo.lon]);
        setRadius(data.radius * 1000); // km → meters
      }
    }
    load();
  }, [data]);

  if (!center) return <p>Loading map...</p>;

  return (
    <MapContainer center={center} zoom={6} style={{ height: "100vh" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

      <Circle
        center={center}
        radius={radius}
        pathOptions={{ color: "#1fd6ff" }}
      />

      <Marker position={center}>
        <Popup>Starting Location</Popup>
      </Marker>

      {destinations.map((d, i) => (
        <Marker key={i} position={[d.lat, d.lon]}>
          <Popup>
            <strong>{d.name}</strong><br />
            <button onClick={() => window.location.href = "/itinerary"}>
              Show Itinerary
            </button>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}
