const API_KEY = "5ae2e3f221c38a28845f05b61d5bacc0f4592c8e6769d47d592aae63";

// ✅ City → Latitude & Longitude (Nominatim)
export async function getCityCoords(city) {
  const res = await fetch(
    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
      city
    )}`,
    {
      headers: {
        "User-Agent": "virtual-travel-assistant"
      }
    }
  );

  const data = await res.json();

  if (!data || data.length === 0) {
    return null;
  }

  return {
    lat: parseFloat(data[0].lat),
    lon: parseFloat(data[0].lon)
  };
}

// ✅ Get nearby tourist places (OpenTripMap)
export async function getPlaces(lat, lon) {
  const res = await fetch(
    `https://api.opentripmap.com/0.1/en/places/radius?radius=10000&lat=${lat}&lon=${lon}&limit=12&rate=2&apikey=${API_KEY}`
  );

  const data = await res.json();

  // Normalize to array
  if (Array.isArray(data)) return data;

  if (data.features) {
    return data.features.map(f => ({
      name: f.properties.name,
      rate: f.properties.rate
    }));
  }

  return [];
}
