import { MapContainer, TileLayer, Marker, Popup, Circle } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function MapView() {
  const startPoint = [12.9716, 77.5946]; // Bangalore
  const radius = 200000; // 200 km

  const destinations = [
    { name: "Mysuru", pos: [12.2958, 76.6394] },
    { name: "Chennai", pos: [13.0827, 80.2707] }
  ];

  return (
    <div style={{ height: "100vh" }}>
      <MapContainer center={startPoint} zoom={6} style={{ height: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Radius Circle */}
        <Circle
          center={startPoint}
          radius={radius}
          pathOptions={{ color: "#1fd6ff" }}
        />

        {/* Starting Point */}
        <Marker position={startPoint}>
          <Popup>Starting Location</Popup>
        </Marker>

        {/* Destination Pins */}
        {destinations.map((d, i) => (
          <Marker key={i} position={d.pos}>
            <Popup>
              <h4>{d.name}</h4>
              <p>Click below to see itinerary</p>
              <button
                onClick={() => window.location.href = "/itinerary"}
              >
                Show Itinerary
              </button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
