const API_KEY = "5ae2e3f221c38a28845f05b61d5bacc0f4592c8e6769d47d592aae63";

// ✅ City → Latitude & Longitude (Nominatim)
export async function getCityCoords(city) {
  const res = await fetch(
    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
      city
    )}`,
    {
      headers: {
        "User-Agent": "virtual-travel-assistant"
      }
    }
  );

  const result = await res.json();

  if (!result || result.length === 0) {
    return null;
  }

  return {
    lat: Number(result[0].lat),
    lon: Number(result[0].lon)
  };
}

// ✅ Get nearby tourist places (OpenTripMap)
// radius MUST be in METERS
export async function getPlaces(lat, lon, radiusMeters) {
  const res = await fetch(
    `https://api.opentripmap.com/0.1/en/places/radius?radius=${radiusMeters}&lat=${lat}&lon=${lon}&limit=25&rate=1&apikey=${API_KEY}`
  );

  const data = await res.json();

  if (!data.features) return [];

  return data.features.map(p => ({
    name: p.properties.name || "Tourist Attraction",
    rate: p.properties.rate ?? "N/A",
    lat: p.geometry.coordinates[1],
    lon: p.geometry.coordinates[0]
  }));
}
