const API_KEY = "5ae2e3f221c38a28845f05b61d5bacc0f4592c8e6769d47d592aae63"; // ← put your key here

// 1. Get city latitude & longitude
export async function getCityCoords(city) {
  const res = await fetch(
    `https://api.opentripmap.com/0.1/en/places/geoname?name=${city}&apikey=${API_KEY}`
  );
  const data = await res.json();

  if (!data || !data.lat) return null;

  return { lat: data.lat, lon: data.lon };
}

// 2. Get nearby tourist places
export async function getPlaces(lat, lon) {
  const res = await fetch(
    `https://api.opentripmap.com/0.1/en/places/radius?radius=10000&lat=${lat}&lon=${lon}&limit=12&rate=2&format=json&apikey=${API_KEY}`
  );
  return await res.json();
}
