import { MapContainer, TileLayer, Marker, Circle, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useEffect, useState } from "react";
import { getCityCoords, getPlaces } from "../services/openTripMap";
import { useNavigate } from "react-router-dom";
import L from "leaflet";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

// ‚úÖ FIX LEAFLET ICON ISSUE
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: markerIcon,
  shadowUrl: markerShadow
});

export default function MapView() {
  const navigate = useNavigate();
  const data = JSON.parse(localStorage.getItem("planTravelData"));

  const [center, setCenter] = useState([20, 78]);
  const [places, setPlaces] = useState([]);
  const [radiusMeters, setRadiusMeters] = useState(0);

  useEffect(() => {
    async function loadMap() {
      if (!data?.location || !data?.radius) return;

      const coords = await getCityCoords(data.location);
      if (!coords) return;

      const radiusInMeters = Number(data.radius) * 1000;

      setCenter([coords.lat, coords.lon]);
      setRadiusMeters(radiusInMeters);

      const result = await getPlaces(coords.lat, coords.lon, radiusInMeters);
      setPlaces(result);
    }

    loadMap();
  }, [data]);

  return (
    <div style={{ height: "90vh" }}>
      <MapContainer center={center} zoom={6} style={{ height: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Radius Circle */}
        {radiusMeters > 0 && (
          <Circle
            center={center}
            radius={radiusMeters}
            pathOptions={{ color: "#00eaff" }}
          />
        )}

        {/* Tourist Place Pins */}
        {places.map((p, i) => (
          <Marker key={i} position={[p.lat, p.lon]}>
            <Popup>
              <strong>{p.name}</strong><br />
              {p.kinds && <small>{p.kinds.split(",").join(", ")}</small>}
              <br /><br />
              <button
                onClick={() => {
                  localStorage.setItem(
                    "selectedPlace",
                    JSON.stringify({
                      location: p.name,
                      days: data.days
                    })
                  );
                  navigate("/itinerary");
                }}
              >
                üìç Show Itinerary
              </button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
