import { MapContainer, TileLayer, Marker, Circle, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useEffect, useState } from "react";
import { getCityCoords, getPlaces } from "../services/openTripMap";
import { useNavigate } from "react-router-dom";

export default function MapView() {
  const navigate = useNavigate();
  const data = JSON.parse(localStorage.getItem("planTravelData"));

  const [center, setCenter] = useState([20, 78]); // default India
  const [places, setPlaces] = useState([]);
  const [radiusMeters, setRadiusMeters] = useState(0);

  useEffect(() => {
    async function loadMapData() {
      if (!data?.location || !data?.radius) return;

      const coords = await getCityCoords(data.location);
      if (!coords) return;

      setCenter([coords.lat, coords.lon]);
      setRadiusMeters(Number(data.radius) * 1000);

      const result = await getPlaces(coords.lat, coords.lon);
      setPlaces(result);
    }

    loadMapData();
  }, [data]);

  return (
    <div style={{ height: "90vh", width: "100%" }}>
      <MapContainer center={center} zoom={6} style={{ height: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Radius Circle */}
        {radiusMeters > 0 && (
          <Circle center={center} radius={radiusMeters} />
        )}

        {/* Place Pins */}
        {places.map((p, i) => (
          <Marker
            key={i}
            position={[p.lat, p.lon]}
          >
            <Popup>
              <strong>{p.name}</strong><br />
              ‚≠ê {p.rate || "N/A"}<br /><br />
              <button
                onClick={() => {
                  localStorage.setItem(
                    "selectedPlace",
                    JSON.stringify({ location: p.name, days: data.days })
                  );
                  navigate("/itinerary");
                }}
              >
                Show Itinerary
              </button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
