import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function PlanTravel() {
  const nav = useNavigate();

  const [form, setForm] = useState({
    location: "",
    radius: "",
    budget: "",
    currency: "",
    days: "",
    tripType: ""
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit() {
    localStorage.setItem("planTravelData", JSON.stringify(form));
    nav("/map");
  }

  return (
    <div className="form-container">
      <h2>Plan Your Travel</h2>

      <div className="form-grid">
        <input name="location" placeholder="Starting Location" onChange={handleChange} />
        <input name="radius" placeholder="Search Radius (km)" onChange={handleChange} />
        <input name="budget" placeholder="Budget" onChange={handleChange} />

        <select name="currency" onChange={handleChange}>
          <option>Currency</option>
          <option>INR</option>
          <option>USD</option>
          <option>EUR</option>
        </select>

        <input name="days" placeholder="Trip Duration (days)" onChange={handleChange} />

        <select name="tripType" onChange={handleChange}>
          <option>Trip Type</option>
          <option>Family</option>
          <option>Solo</option>
          <option>Couple</option>
          <option>Friends</option>
        </select>
      </div>

      <div className="form-actions">
        <button className="primary-btn" onClick={handleSubmit}>
          Show Map
        </button>
      </div>
    </div>
  );
}
