import { touristData } from "../data/touristData";

export default function Itinerary() {
  const data =
    JSON.parse(localStorage.getItem("selectedPlace")) ||
    JSON.parse(localStorage.getItem("fixedData"));

  if (!data || !data.location) {
    return <h2>No itinerary data found</h2>;
  }

  const city = data.location;
  const days = data.days || 3;

  const places =
    touristData[city] || [
      "City Exploration",
      "Local Attractions",
      "Food & Culture Tour"
    ];

  return (
    <div className="itinerary-container">
      <h2 className="itinerary-title">
        Your {city} Itinerary
      </h2>

      {places.slice(0, days).map((place, i) => (
        <div key={i} className="day-card">
          <h3>Day {i + 1}</h3>
          <p>üìç {place}</p>
        </div>
      ))}

      <div className="itinerary-actions">
        <button className="primary-btn">üíæ Save</button>
        <button className="primary-btn">üîÑ Regenerate</button>
        <button className="primary-btn">‚ùå Discard</button>
      </div>
    </div>
  );
}
