import { useEffect, useState } from "react";
import { getCityCoords, getPlaces } from "../services/openTripMap";

export default function Itinerary() {
  const data =
    JSON.parse(localStorage.getItem("fixedData")) ||
    JSON.parse(localStorage.getItem("planTravelData"));

  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function loadItinerary() {
      try {
        if (!data || !data.location) {
          throw new Error("No destination provided");
        }

        const coords = await getCityCoords(data.location);
        if (!coords) {
          throw new Error("City not found");
        }

        const result = await getPlaces(coords.lat, coords.lon);
        if (!result || result.length === 0) {
          throw new Error("No places found");
        }

        setPlaces(result.slice(0, data.days || 3));
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    loadItinerary();
  }, [data]);

  if (loading) {
    return <h2 style={{ textAlign: "center" }}>Generating itinerary...</h2>;
  }

  if (error) {
    return (
      <div style={{ textAlign: "center" }}>
        <h2>‚ö†Ô∏è Unable to generate itinerary</h2>
        <p>{error}</p>
        <p>Try another city like London or Mumbai.</p>
      </div>
    );
  }

  return (
    <div className="itinerary-container">
      <h2 className="itinerary-title">
        Your {data.location} Itinerary
      </h2>

      {places.map((p, i) => (
        <div key={i} className="day-card">
          <h3>Day {i + 1}</h3>
          <p>üìç {p.name}</p>
          <p>‚≠ê Rating: {p.rate ?? "N/A"}</p>
        </div>
      ))}
    </div>
  );
}
